import React, { Component } from "react";

class Counter extends Component {
  state = {
    value: this.props.counter.value,
    tgs: ["1", "2", "3"],
  };

  change = () => {
    this.setState({ value: this.state.value + 1 });
  };

  renderText() {
    if (this.state.tgs.length === 0) return <p>There are No text</p>;
  }
  render() {
    return (
      <React.Fragment>
        <span className={this.getBadgeClass()}>{this.formatCuont()} </span>
        <button onClick={this.change} className="btn btn-dark">
          Incremet
        </button>
        <button
          onClick={() => this.props.onDelete(this.props.counter.id)}
          className="btn btn-danger btn-sm m-2"
        >
          Delete
        </button>
        <button
          onClick={() => this.props.onReset()}
          className="btn btn-info btn-sm m-2"
        >
          Reset
        </button>
        <br />
        <div>
          {this.state.tgs.length === 0 && "Please create a new tag"}
          {this.renderText()}
        </div>
      </React.Fragment>
    );
  }

  getBadgeClass() {
    let classess = "m-2 text-";
    classess += this.props.counter.value === 0 ? "danger" : "info";
    return classess;
  }
  formatCuont() {
    const { value } = this.state;
    return value === 0 ? "Zero" : value;
  }
}

export default Counter;
