import React, { Component } from "react";

class Counter extends Component {
  state = {
    count: 0,
    tgs: ["1", "2", "3"],
  };

  change = () => {
    this.setState({ count: this.state.count + 1 });
  };

  renderText() {
    if (this.state.tgs.length === 0) return <p>There are No text</p>;

    return (
      <ul>
        {this.state.tgs.map((tgs) => (
          <li key={tgs}>{tgs}</li>
        ))}
      </ul>
    );
  }
  render() {
    return (
      <React.Fragment>
        <h1>Incremet</h1>
        <span className={this.getBadgeClass()}>{this.formatCuont()} </span>
        <button onClick={this.change} className="btn btn-dark">
          Incremet
        </button>

        <br />

        <div>
          {this.state.tgs.length === 0 && "Please create a new tag"}
          {this.renderText()}
        </div>
        <button onClick={this.reset()} className="btn btn-danger">
          Reset
        </button>
      </React.Fragment>
    );
  }

  getBadgeClass() {
    let classess = "m-2 text-";
    classess += this.state.count == 0 ? "danger" : "info";
    return classess;
  }
  formatCuont() {
    const { count } = this.state;
    return count == 0 ? "Zero" : count;
  }
  reset() {
    return (this.state.count = 0);
  }
}

export default Counter;
