import React, { Component } from "react";
import Counter from "./counter";

export default class Counters extends Component {
  state = {
    counnters: [
      { id: 1, value: 0 },
      { id: 2, value: 0 },
      { id: 3, value: 0 },
      { id: 4, value: 0 },
    ],
  };

  delete = (cId) => {
    const c = this.state.counnters.filter((c) => c.id !== cId);
    this.setState({ counters });
  };

  render() {
    return (
      <div>
        <h1>Increment</h1>

        {this.state.counnters.map((counnter) => (
          <Counter
            key={counnter.id}
            onDelete={this.delete}
            value={counnter.value}
            id={counnter.id}
          ></Counter>
        ))}
      </div>
    );
  }
}
