import React, { Component } from "react";

class Counter extends Component {
  state = {
    count: 0,
    tgs: ["1", "2", "3"],
  };
  renderText() {
    if (this.state.tgs.length === 0) return <p>There are No text</p>;

    return (
      <ul>
        {this.state.tgs.map((tgs) => (
          <li key={tgs}>{tgs}</li>
        ))}
      </ul>
    );
  }
  render() {
    return (
      <React.Fragment>
        <h1>Incremet</h1>
        <span className={this.getBadgeClass()}>{this.formatCuont()} </span>
        <button className="btn btn-dark">Incremet</button>
        <div>{this.renderText()} </div>
      </React.Fragment>
    );
  }

  getBadgeClass() {
    let classess = "m-2 text-";
    classess += this.state.count == 0 ? "danger" : "info";
    return classess;
  }
  formatCuont() {
    const { count } = this.state;
    return count == 0 ? "Zero" : count;
  }
}

export default Counter;
