import React, { Component } from "react";

class Counter extends Component {
  state = {
    count: 0,
    tgs: ["tag1", "tag2", "tag3"],
  };
  render() {
    return (
      <React.Fragment>
        <h1>Incremet</h1>
        <span className={this.getBadgeClass()}>{this.formatCuont()} </span>
        <button className="btn btn-dark">Incremet</button>
        <ul>
          {this.state.tgs.map((tgs) => (
            <li key={tgs.id}>{tgs}</li>
          ))}
        </ul>
      </React.Fragment>
    );
  }

  getBadgeClass() {
    let classess = "m-2 text-";
    classess += this.state.count == 0 ? "danger" : "info";
    return classess;
  }
  formatCuont() {
    const { count } = this.state;
    return count == 0 ? "Zero" : count;
  }
}

export default Counter;
